INSERT INTO `qk7ce_komento_comments` VALUES(1, 'com_content', 118, 'The author did a great job with all these research work. Really valuable information, thank you!', 'James Bernard', '', 'james_bernard@gmail.com', '', '127.0.0.1', 0, '2014-05-03 09:58:29', 846, '2015-10-15 12:20:08', 0, '0000-00-00 00:00:00', 0, 0, '2015-02-25 13:41:36', '2016-04-01 05:36:34', 0, 0, 0, 1, 4, 0, '', '', '', '{}', 0);
