INSERT INTO `qk7ce_komento_comments` VALUES(3, 'com_content', 118, 'That’s awesome! Future belongs to youngsters, so businessmen can’t ignore their needs in any case.', 'Demo User', '', 'demo@demoolink.org', '', '127.0.0.1', 846, '2014-05-03 10:02:21', 846, '2015-10-15 12:20:52', 0, '0000-00-00 00:00:00', 0, 0, '2014-05-03 10:02:21', '2016-04-01 05:36:27', 0, 0, 2, 6, 7, 1, '', '', '', '{}', 0);
